import React from 'react';
const { compose, withProps, withStateHandlers } = require('recompose');
const {
  withScriptjs,
  withGoogleMap,
  GoogleMap,
  Marker,
} = require('react-google-maps');
const { InfoBox } = require('react-google-maps/lib/components/addons/InfoBox');

const Map = compose(
  withProps({
    googleMapURL:
      'https://maps.googleapis.com/maps/api/js?key=AIzaSyC5S9ELt2PdwzlZMRWCGQNh7ZKj8j-7qzY&v=3.exp&libraries=geometry,drawing,places',
    loadingElement: <div style={{ height: `100%` }} />,
    containerElement: <div style={{ height: `400px` }} />,
    mapElement: <div style={{ height: `100%` }} />,
    center: { lat: 41.14961, lng: -8.61099 },
  }),
  withStateHandlers(
    () => ({
      isOpen: false,
    }),
    {
      onToggleOpen: (isOpen: any) => () => ({
        isOpen: !isOpen,
      }),
    }
  ),
  withScriptjs,
  withGoogleMap
)(
  (props: {
    center: { lat: number; lng: number };
    onToggleOpen: any;
    isOpen: any;
  }) => (
    <GoogleMap
      defaultZoom={70}
      defaultCenter={props.center}
      // defaultOptions={{ styles: demoFancyMapStyles }}
    >
      <InfoBox
        defaultPosition={
          new google.maps.LatLng(props.center.lat, props.center.lng)
        }
        options={{ closeBoxURL: ``, enableEventPropagation: true }}
      >
        <div
          style={{ backgroundColor: `yellow`, opacity: 0.75, padding: `12px` }}
        >
          <div style={{ fontSize: `16px` }}>Hello, Taipei!</div>
        </div>
      </InfoBox>
      <Marker
        position={{ lat: 22.6273, lng: 120.3014 }}
        onClick={props.onToggleOpen}
      >
        {props.isOpen && (
          <InfoBox
            onCloseClick={props.onToggleOpen}
            options={{ closeBoxURL: ``, enableEventPropagation: true }}
          >
            <div
              style={{
                backgroundColor: `yellow`,
                opacity: 0.75,
                padding: `12px`,
              }}
            >
              <div style={{ fontSize: `16px` }}>Hello, Kaohsiung!</div>
            </div>
          </InfoBox>
        )}
      </Marker>
    </GoogleMap>
  )
);

export { Map };
