import React, { useState, useEffect } from 'react';
const { compose, withProps, withStateHandlers } = require('recompose');
const {
  withScriptjs,
  withGoogleMap,
  GoogleMap,
  Marker,
  DirectionsRenderer,
} = require('react-google-maps');
const { InfoBox } = require('react-google-maps/lib/components/addons/InfoBox');

function MapDirectionsRenderer(props: { places: any; travelMode: any }) {
  const [directions, setDirections] = useState(null);
  const [error, setError] = useState<any>(null);

  useEffect(() => {
    const { places, travelMode } = props;
    navigator.geolocation.getCurrentPosition(success);
    const waypoints: any[] = [];
    places.forEach((p: any) => {
      waypoints.push({
        location: { lat: p.latitude, lng: p.longitude },
        stopover: true,
      });
    });

    const origin = waypoints.shift().location;
    const destination = waypoints.pop().location;

    const directionsService = new google.maps.DirectionsService();
    directionsService.route(
      {
        origin: origin,
        destination: destination,
        travelMode: travelMode,
        waypoints: waypoints,
      },
      (result: any, status) => {
        console.log(result);
        if (status === google.maps.DirectionsStatus.OK) {
          setDirections(result);
        } else {
          setError(result);
        }
      }
    );
  });

  console.log(currentPosition);

  if (error) {
    return <h1>{error?.status}</h1>;
  }
  return directions && <DirectionsRenderer directions={directions} />;
}

const Map = compose(
  withProps({
    googleMapURL: `https://maps.googleapis.com/maps/api/js?key=${process.env.REACT_APP_GOOGLE_MAPS_API_KEY}&v=3.exp&libraries=geometry,drawing,places`,
    loadingElement: <div style={{ height: `100%` }} />,
    containerElement: <div style={{ height: `400px` }} />,
    mapElement: <div style={{ height: `100%` }} />,
    center: currentPosition,
  }),
  withStateHandlers(
    () => ({
      isOpen: false,
    }),
    {
      onToggleOpen: (isOpen: any) => () => ({
        isOpen: !isOpen,
      }),
    }
  ),
  withScriptjs,
  withGoogleMap
)(
  (props: {
    center: { lat: number; lng: number };
    onToggleOpen: any;
    isOpen: any;
    markers: any;
  }) => (
    <GoogleMap defaultZoom={12} defaultCenter={props.center} props>
      <>
        {props.markers.map((marker: any, index: number) => (
          <Marker
            key={index}
            position={{ lat: marker.latitude, lng: marker.longitude }}
          />
        ))}
        <MapDirectionsRenderer
          places={props.markers}
          travelMode={google.maps.TravelMode.DRIVING}
        />
      </>
    </GoogleMap>
  )
);

export { Map };
